import { useState, useEffect } from "react"
import "./App.css"

function App(){

    const [options, setOptions]=useState([])
    const [from, setFrom]=useState('en')
    const [to, setTo] = useState('en')
    const [text, setText] = useState("")

    useEffect(()=>{
        fetch("https://libretranslate.com/languages")
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            setOptions(resp)
        })
    },[])

    useEffect(()=>{
        setInterval(()=>{
            console.log(text)
        }, 10)
    })

    return(
        <div>
            <div id="header">
                <h2>Translataurus</h2>
            </div>
            <div style={{display:"grid", gridTemplateColumns:"auto auto"}}>
                <div>
                    <span>
                        From: 
                        <select>
                            {options.map(item=>(
                                <option>{item.name}</option>
                            ))}
                        </select>

                    </span>
                    <br/><br/>
                    <span>
                        <textarea rows={40} cols={100} placeholder="Enter your text here..." onChange={(e)=>setText(e.target.value)}></textarea>
                    </span>
                </div>
                <div>
                <span>
                        To: 
                        <select>
                            {options && options.map(item=>(
                                <option>{item.name}</option>
                            ))}
                        </select>
                    </span>
                    <br/><br/>
                    <span>
                        <textarea rows={40} cols={100} placeholder="Translated result will be displayed here..."></textarea>
                    </span>
                </div>
            </div>
        </div>
    )
}

export default App
