import { useState, useEffect } from "react"
import "./App.css"

function App(){

    const [options, setOptions]=useState([])
    const [from, setFrom]=useState('en')
    const [to, setTo] = useState('en')
    const [text, setText] = useState("")
    const [result, setResult] = useState("")

    useEffect(()=>{
        fetch("https://libretranslate.com/languages")
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            setOptions(resp)
        })
    },[])

    const translateText=()=>{
        let data={
            q : text,
            source : from,
            target: to
        }
        fetch("https://libretranslate.de/translate",{
            method:"POST",
            headers:{"Content-type":"application/json"},
            body:JSON.stringify(data)
        })
        .then((resp)=>{
            return resp.json()
        })
        .then((res)=>{
            setResult(res.translatedText)
            console.log(result)
        })
        .catch((err)=>{
            console.log(err)
        })
    }

    return(
        <div>
            <div id="header">
                <h2>Translataurus</h2>
                <button onClick={translateText} style={{width:"150px" ,color:"white", backgroundColor:"blue", padding:"10px", borderRadius:"10px", marginBottom:"20px", border:"none"}}>Translate</button>
            </div>
            <div style={{display:"grid", gridTemplateColumns:"auto auto"}}>
                <div>
                    <span>
                        From: 
                        <select onChange={(e)=>setFrom(e.target.value)}>
                            {options.map(item=>(
                                <option value={item.code}>{item.name}</option>
                            ))}
                        </select>
                    </span>
                    <br/><br/>
                    <span>
                        <textarea rows={37} cols={100} placeholder="Enter your text here..." onChange={(e)=>setText(e.target.value)}></textarea>
                    </span>
                </div>
                <div>
                <span>
                        To: 
                        <select onChange={(e)=>setTo(e.target.value)}>
                            {options && options.map(item=>(
                                <option value={item.code}>{item.name}</option>
                            ))}
                        </select>
                    </span>
                    <br/><br/>
                    <span>
                        <textarea rows={37} cols={100} placeholder="Translated result will be displayed here...">{result}</textarea>
                    </span>
                </div>
            </div>
        </div>
    )
}

export default App
